    private fun setupViewModel() {
        viewModel = ViewModelProviders.of(
            this,
            ViewModelActivityFactory(ApiActivityHelper(accessToken))
        ).get(ActivityViewModel::class.java)
    }

    private fun setupObserversStreamsActivity(id: Long){
        viewModel.getStreamsActivity(id).observe(this, Observer {
            it?.let { resource ->
                when (resource.status) {
                    Status.SUCCESS -> {
                        resource.data?.let { data ->
                            Log.d("okhttp",data.toString())
                            data.latlng?.data?.let { dataLaLo ->
                                initMapbox(dataLaLo)
                            }
                        }
                    }
                    Status.ERROR -> {

                        Toast.makeText(this, it.message + "streams", Toast.LENGTH_LONG).show()
                        Log.d("okhttp",it.toString())
                    }
                    Status.LOADING -> {

                    }
                }
            }
        })
    }

